import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import {Logo} from '../../logo.js';
import axios from 'axios';
import './login.css';

class LoginForm extends Component {

    handleSubmit = async (event) => {
        event.preventDefault();
        const dataSet = [
            {id : event.target.loginId.value},
            {pw : event.target.password.value}
        ]
        window.location.href = "/";
        const res = await axios.post(this.props.menu.req,dataSet);
        //1. login
        if (res.data.token!==false){
            alert('로그인 성공');
            window.location.href = "/";
            const token = res.data.token;
            localStorage.setItem('jwtToken', token);
        }        
        //2. findId
        //console.log(res.data.id) -> module.

        //3. findPw
        //console.log(res.data.pw) -> module.
    }

    render() {
        const {menu} = this.props;
        return (
            <div className="containerBox">
                <a href='./'><Logo/></a>
                <h2>{menu.subject}</h2>
                <div id="loginForm">
                    <form onSubmit={this.handleSubmit}>
                        <label htmlFor="loginId">{menu.id}</label><br/>
                        <input
                            type="text"
                            id="username"
                            name="loginId"
                            placeholder={menu.id+'를 입력하세요'}
                            onChange={this.handleIdChange}
                            required
                        /><p/>
                        <br/>
                        <label htmlFor="password">{menu.pw}</label><br/>
                        <input
                            type={menu.inputType}
                            id="password"
                            name="password"
                            placeholder={menu.pw+'를 입력하세요'}
                            onChange={this.handlePwChange}
                            required
                        /><br/><br/><br/><br/>
                        <button type="submit">{menu.subject}</button>
                        <br/><br/><br/>
                        <nav id="bottomNav">
                            <li><Link to="/newAccount" id="newAccount">회원가입</Link></li>
                            <li>
                                <Link to="/findID" className="findMy">아이디 찾기</Link>
                                <span>  </span>
                                <Link to="/findPW" className="findMy">비밀번호 찾기</Link>
                            </li>
                        </nav>
                    </form>
                </div>
            </div>
        );
    }
}

export default LoginForm;
