import React, { useEffect } from 'react';
import { Frame } from './components/frame/frame.js';

import './maps.css';

function NaverMaps() {
  useEffect(() => {
    const script = document.createElement('script');
    script.async = true;
    script.src = 'https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=di9lw1dnxq';

    script.onload = () => {
      const map = new window.naver.maps.Map('map', {
        center: new window.naver.maps.LatLng(37.3595704, 127.105399),
        zoom: 10
    });

    const markers = [
      {
        position: new window.naver.maps.LatLng(37.5666103, 126.9783882),
        title: '서울',
      },
      {
        position: new window.naver.maps.LatLng(37.4979509, 127.0276367),
        title: '강남',
      },
      {
        position: new window.naver.maps.LatLng(37.5514544, 126.9882244),
        title: '홍대',
      },
      {
        position: new window.naver.maps.LatLng(37.5414544, 127.1882244),
        title: '강동대충',
      },
      {
        position: new window.naver.maps.LatLng(37.4414544, 127.2282244),
        title: '5',
      },
    ];

   //n개의 마커 추가 기능, 마커의 coord계산 등... 

    markers.forEach((markerInfo) => {
      const marker = new window.naver.maps.Marker({
        position: markerInfo.position,
        map: map,
        title: markerInfo.title,
      });

    window.naver.maps.Event.addListener(marker, 'click', () => {
      // 클릭된 마커의 정보를 이용하여 원하는 동작 수행
      alert(`마커 클릭: ${marker.position}`);
    });    
  });

    };

    document.head.appendChild(script);
  }, []);


  return (
    <div id="map" style={{ width: '1200px', height: '768px' }}></div>
  );
}


export function Modal(){
  return (
    <div id="modal">
      둔촌사거리
      <input type="text"
      placeholder='날짜'
      />
      <input type="text"
      placeholder='시간'
      />
    </div>
  );
}

export function Maps(){
  return(
    <div>
      <Frame/>
      <div className = "displayRow">
        <div className ="지도검색창">
            <form>
              <input type="text"
              placeholder='주소를 입력해주세요'
              id="주소검색창"/>
            </form>
            <div className='mapBox'>강동</div>
            <div className='mapBox'>강동2</div>
            <div className='mapBox'>강동3</div>
        </div>
        <div className="NaverMaps">
          <NaverMaps/>
          <Modal/>
        </div>
      </div>
    </div>
  );
}
