import { Component } from "react";
import {Frame, Banner, ContentNav, Footer} from '../frame/frame.js';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';

import './board.css';
export default class Post extends Component{
    state = {
        content:{
            title:'test_title',
            contents : 'test 컨텐츠입니다.',
            writer:'test 작성자'
        },
    }

    componentDidMount() {
        this.getDataFromDB();
    }

    getDataFromDB = async () => {
        const searchParams = new URLSearchParams(window.location.search);
        const id = searchParams.get('id');
        const url = '/board/' + id;
        const axiosConfig = {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('jwtToken')}`,
            },
        };
        const res = await axios.get(url,axiosConfig);
        this.setState({content : res.data});
    }

    getCommentFromDB = async () => {
        //const url = '/comment/' + id;
    }

    render(){   
        const {content} =  this.state;
        if (content === null || content === undefined) {
            return <p>Loading...</p>;
        }

    return(
    <div>
        <Frame/>
        <Banner name={'커뮤니티'}/>
        <ContentNav name={'커뮤니티'}/>
        <div className='communityContentBox'>
                <div className="post">
                        <div className="postTitleBox">
                            <div className="postTitle">{content.title}</div>
                            <div className="postTitleDate">{content.writer},date</div>
                        </div>
                        <div className="postContent">{content.contents}</div>
                        <div className="postComment">comment</div>
                </div>
                <br/><br/>
                <Link to="/writing">
                <button className ="btnWriting">수정</button>
                </Link>
        </div>
        <Footer/>
    </div>
    );

    }
}
