//상단바 (Frame) 와 Footer 함수가 정의되어 있음

import './frame.css';
import {Logo, LogoAdd} from '../../logo.js';
import { useState } from 'react';

function 아래화살표(){
  return (
    <img src="iconDown.png"
    width="10px"/>
  );
}

function Bars({i}){ //이걸 그냥 nav에 붙는 bars로 사용하면 TopNav에도 적용할 수 있음.
    const [isActive, setIsActive] = useState(false);
    const handleClick = () => {
      setIsActive(!isActive);
    };

    const menu=[{
      name: "서비스 소개",
      menu1 : "인사말",
      menu2 : "홈페이지 안내",
    },{
      name: "게시판",
      menu1 : "공지사항",
      menu2 : "커뮤니티",
    },{
      name: "데이터 업로드",
      menu1 : "공지사항",
      menu2 : "커뮤니티",
    }
    ]

  return(
    <div>
      <li onClick={handleClick}>{menu[i].name} <아래화살표/></li>
      <div className={`${isActive ? 'visible-element ' : 'hidden-element'}`}>
          <ul className='bars'>
            <li><a href="./인사말">{menu[i].menu1}</a></li>
            <li><a href="./안내">{menu[i].menu2}</a></li>
          </ul>
      </div>
    </div>
  );
}

export function Frame(){

    return(
        <div>
          <div id="user">
            <a href="/login" className ="topNav">로그인</a>
            <a href="/account" className ="topNav">회원가입</a>
          </div>
          <div id="menu">
            <LogoAdd/>
            <nav id ="mainNav">
              <ul className = "horizontal-list">
                <Bars i={0}/>
                <Bars i={1}/>
                <Bars i={2}/>
              </ul>
            </nav>
          </div>
        </div>

    );
  }

export function Banner({name}){

return (
    <div class="banner">
      <h1>{name}</h1>
      <img src="banner.jpg"/>
    </div>
);
}

export function ContentNav({name}){
  return (
    <nav className="contentNav">
        <ul>
            <li id="iconHome">
                <a href="./">
                    <img src="iconHome.png" width="20px"/>
                </a>
            </li>
            <li className="contentNav한칸">
                <a href="./">
                    <p>{name}</p>
                    <아래화살표/>
                </a>
            </li>
            <li className="contentNav한칸">
                <a href="./">
                    <p>인사말</p>
                    <아래화살표/>
                </a>
            </li>
        </ul>
    </nav>
    );
}


export function Footer(){
  return(
      <div className='footer'>
          <div className="footerBox">
              <Logo/>
              <div>
                  <div className="footerInfo">
                      <div>서울시 광진구 능동로 209 세종대학교</div>
                      <div>TEL : 010-@@@@-@@@@</div>
                      <div>Email : @sju.ac.kr</div>
                  </div>
                  <div>Copyright@ 2023 Sejong RDD | All Rights Reserved</div>
              </div>
          </div>
      </div>
  );
}
