import {Frame, Banner, ContentNav, Footer} from '../frame/frame.js';
import './upload.css';
import React, { useState } from 'react';

const ImageUpload = () => {
    const [selectedFiles, setSelectedFiles] = useState([]);

    const handleFileChange = (event) => {
      // 여러 파일이 선택될 때 호출되는 함수
      const files = event.target.files;
      setSelectedFiles(files);
    };

  // 파일 업로드 버튼 클릭 시 호출되는 이벤트 핸들러
  const handleUpload = () => {
    // 파일을 업로드하는 함수

    const formData = 'data';
    if (selectedFiles.length > 0) {
      //const formData = new FormData();

      for (let i = 0; i < selectedFiles.length; i++) {
        formData.append('files', selectedFiles[i]);
      }

      fetch('/upload', {
        method: 'POST',
        body: formData,
      })
        .then(response => response.json())
        .then(data => {
          // 서버로부터의 응답 처리
          console.log('Upload successful:', data);
        })
        .catch(error => {
          // 오류 처리
          console.error('Error uploading files:', error);
        });
    } else {
      console.error('No files selected.');
    }
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} multiple />
      <button onClick={handleUpload}>업로드</button>
    </div>
  );
};


function Upload(){
    return(
        <div>
        <Frame/>
        <Banner name={'데이터 업로드'}/>
        <ContentNav/>
            <div className='uploadContentBox'>
                <div className="uploadBox">
                    <p id="업로드설명">파일 업로드 관련 설명</p>
                    <div className="fileBox">
                        <img src="icon구름.png" width="60px"/>
                        <div className="업로드부가설명">파일을 선택하거나 드래그하여 올려주세요</div>
                        <div className="업로드부가설명 회색">10MB이하의 JPG파일만 업로드 가능</div>
                        <ImageUpload/>
                    </div>
                    <div>
                        <button>제출</button>
                        <button>취소</button>
                    </div>
                </div>

                <div className="uploadBox">데이터 업로드 화면</div>
            </div>
        <Footer/>
        </div>
    );
}


export default Upload;
