import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { LineChart, Line } from 'recharts';
import React, { useState, useEffect } from 'react';

const data = [
    {
      name: '00',
      uv: 107,
      pv: 2400,
      amt: 2400,
    },
    {
      name: '03',
      uv: 99,
      pv: 1398,
      amt: 2210,
    },
    {
      name: '06',
      uv: 80,
      pv: 9800,
      amt: 2290,
    },
    {
      name: '09',
      uv: 111,
      pv: 3908,
      amt: 2000,
    },
    {
      name: '12',
      uv: 127,
      pv: 4800,
      amt: 2181,
    },
    {
      name: '15',
      uv: 144,
      pv: 3800,
      amt: 2500,
    },
    {
      name: '18',
      uv: 210,
      pv: 4300,
      amt: 2100,
    },{
      name: '21',
      uv: 250,
      pv: 4300,
      amt: 2100,
    },{
      name: '24',
      uv: 300,
      pv: 4300,
      amt: 2100,
    }
  ];




function RenderChart(){
  const [data2, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/chart'); // 서버 API 엔드포인트에 맞게 수정
        const result = await response.json();
        setData(result);
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };
    fetchData();
  }, []);


  const [isActive, setToggleState] = useState(false);
  const handleButtonClick = () => {
      // 현재 버튼 상태에 따라 반대의 상태로 변경
      setToggleState(!isActive);
    };

  const buttonClass = isActive ? '투명화' : '불투명화';
  const buttonClass2 = isActive ? '불투명화' : '투명화';
  const 보일까안보일까 = isActive ? '보인다' : '안보인다';
  const 안보일까보일까 = isActive ? '안보인다' : '보인다';

  return(

    <div className="차트">
      <div>차량별 이동량</div>
      <div className="토글버튼">
        <button className={buttonClass} id="혼잡도버튼" onClick={handleButtonClick}>혼잡도</button>
        <button className={buttonClass2} id="차량버튼" onClick={handleButtonClick}>차량</button>
      </div>
      <br/><br/>
      <div className={보일까안보일까}>
        <RenderBarChart data={data}/>
      </div>
      <div className={안보일까보일까}>
        <RenderStringChart data={data}/>
      </div>
    </div>
  );
}

function RenderBarChart({data}){
    return(
      <BarChart width={450} height={350} data={data}>
        <Bar dataKey="uv" fill="#8884d8" />
        <CartesianGrid stroke="#ccc" />
        <XAxis dataKey="name" />
        <YAxis />
      </BarChart>
    );   
}

function RenderStringChart({data}){

return (
    <LineChart width={450} height={350} data={data}>
      <Line type="monotone" dataKey="uv" stroke="#8884d8" />
      <CartesianGrid stroke="#ccc" />
      <XAxis dataKey="name" />
      <YAxis />
    </LineChart>
  );

}

export default RenderChart;
