import React, { Component } from 'react';
import Logo from './logo.js';
import { useState } from 'react';

import './newAccountGeneral.css';


class NewAccoutGeneral extends Component {
    constructor(props) {
        super(props);
        /*this.state = {
            username: '',
            password: '',
        };*/
    }

    handleInputChange = (event) => {
        /*
        const { name, value } = event.target;
        this.setState({ [name]: value }); */
    }

    handleSubmit = (event) => {
        event.preventDefault();

        //서버로 데이터 보냄
        alert('hi');

        //console.log('아이디:', this.state.username); 실제 웹 콘솔
        //console.log('비밀번호:', this.state.password);
    }



    render(){

    const 테스트정보 = {
        id:'아이디',
        inputBox:2,
        visible:true,
        btn:false,
    }
    const 바뀌는정보 = [
        '아이디','비밀번호','이름','전화번호','이메일'
    ];
    return (
        <div>
            <Logo/>
            <h2>일반회원가입</h2>
            <div className="회원가입정보">
                <form>
                    <Input한칸 data={바뀌는정보[0]}/>
                    <Input한칸 data={바뀌는정보[1]}/> 
                    <Input한칸 data={바뀌는정보[2]}/> 
                    <Input한칸 data={바뀌는정보[3]}/>
                    <Input한칸 data={바뀌는정보[4]}/> 
                </form>
            </div>
        </div>
    );
}
}

function Input한칸({data}){
    return (
        <div className ="회원가입정보한칸">
        <p>{data}</p>
        <div>
            <div id='아이디버튼'>
                <input
                type="text"
                id="username"
                name="username"
                placeholder='아이디를 입력하세요'
                //value={this.state.username}
                //onChange={this.handleInputChange}
                required/>
                <button>중복체크</button>
            </div>
            <p className="경고메시지">영문, 숫자만 조합 (영문 필수 포함) 어쩌구 저쩌구</p>
        </div>
    </div>
    );
}

export default NewAccoutGeneral;
